import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [wallets, setWallets] = useState([]);
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [walletsRes, transactionsRes] = await Promise.all([
        axios.get('http://localhost:8000/api/wallets/'),
        axios.get('http://localhost:8000/api/transactions/')
      ]);

      setWallets(walletsRes.data);
      setTransactions(transactionsRes.data.slice(0, 10));
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const totalBalance = wallets.reduce((sum, wallet) => {
    const rates = { NGN: 0.0024, KES: 0.0075, BTC: 45000, ETH: 3000 };
    return sum + (parseFloat(wallet.balance) * (rates[wallet.currency] || 1));
  }, 0);

  if (loading) {
    return (
      <div className="min-h-screen bg-slate-50 flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">

        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-slate-900 mb-2">Dashboard</h1>
          <p className="text-slate-600">Manage your multi-currency portfolio</p>
        </div>

        {/* Total Balance Card */}
        <div className="mb-8">
          <div className="bg-gradient-to-r from-blue-600 to-purple-600 rounded-2xl p-8 text-white shadow-xl">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-blue-100 text-sm font-medium mb-2">Total Portfolio Value</p>
                <h2 className="text-4xl font-bold mb-1">${totalBalance.toFixed(2)}</h2>
                <p className="text-blue-100 text-sm">Across all currencies</p>
              </div>
              <div className="w-16 h-16 bg-white/10 rounded-full flex items-center justify-center">
                <i className="bi bi-wallet2 text-2xl"></i>
              </div>
            </div>
          </div>
        </div>

        {/* Wallet Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          {wallets.map(wallet => {
            const getCurrencyColor = (currency) => {
              const colors = {
                NGN: 'from-green-500 to-emerald-600',
                KES: 'from-orange-500 to-amber-600',
                BTC: 'from-yellow-500 to-orange-600',
                ETH: 'from-blue-500 to-indigo-600'
              };
              return colors[currency] || 'from-gray-500 to-slate-600';
            };

            return (
              <div key={wallet.id} className="bg-white rounded-xl p-6 shadow-lg hover:shadow-xl transition-shadow">
                <div className="flex items-center justify-between mb-4">
                  <div className={`w-12 h-12 bg-gradient-to-r ${getCurrencyColor(wallet.currency)} rounded-lg flex items-center justify-center`}>
                    <i className="bi bi-currency-exchange text-white text-lg"></i>
                  </div>
                  <span className="text-sm font-medium text-slate-500">{wallet.currency}</span>
                </div>
                <h3 className="text-2xl font-bold text-slate-900 mb-1">
                  {parseFloat(wallet.balance).toFixed(wallet.currency.includes('BTC') || wallet.currency.includes('ETH') ? 6 : 2)}
                </h3>
                <p className="text-slate-500 text-sm">{wallet.currency} Wallet</p>
              </div>
            );
          })}
        </div>

        {/* Quick Actions */}
        <div className="bg-white rounded-xl p-6 shadow-lg mb-8">
          <h3 className="text-xl font-bold text-slate-900 mb-6">Quick Actions</h3>
          <div className="grid grid-cols-2 lg:grid-cols-4 gap-4">
            {[
              { icon: 'send', label: 'Send Money', color: 'blue' },
              { icon: 'plus-circle', label: 'Deposit', color: 'green' },
              { icon: 'dash-circle', label: 'Withdraw', color: 'red' },
              { icon: 'arrow-repeat', label: 'Convert', color: 'purple' }
            ].map(({ icon, label, color }) => (
              <button key={label} className={`p-4 rounded-xl border-2 border-dashed border-${color}-200 hover:border-${color}-400 hover:bg-${color}-50 transition-all group`}>
                <i className={`bi bi-${icon} text-2xl text-${color}-600 block mb-2 group-hover:scale-110 transition-transform`}></i>
                <span className={`text-sm font-medium text-${color}-700`}>{label}</span>
              </button>
            ))}
          </div>
        </div>

        {/* Recent Transactions */}
        <div className="bg-white rounded-xl shadow-lg">
          <div className="p-6 border-b border-slate-200">
            <h3 className="text-xl font-bold text-slate-900">Recent Activity</h3>
          </div>

          {transactions.length > 0 ? (
            <div className="divide-y divide-slate-200">
              {transactions.map(transaction => (
                <div key={transaction.id} className="p-6 hover:bg-slate-50 transition-colors">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-4">
                      <div className={`w-12 h-12 rounded-full flex items-center justify-center ${
                        parseFloat(transaction.amount) >= 0 
                          ? 'bg-green-100 text-green-600' 
                          : 'bg-red-100 text-red-600'
                      }`}>
                        <i className={`bi ${
                          parseFloat(transaction.amount) >= 0 ? 'bi-arrow-up-right' : 'bi-arrow-down-left'
                        } text-lg`}></i>
                      </div>
                      <div>
                        <h4 className="font-medium text-slate-900">{transaction.type}</h4>
                        <p className="text-sm text-slate-500">
                          {new Date(transaction.created_at).toLocaleDateString()}
                        </p>
                      </div>
                    </div>
                    <div className="text-right">
                      <div className={`font-bold ${
                        parseFloat(transaction.amount) >= 0 ? 'text-green-600' : 'text-red-600'
                      }`}>
                        {parseFloat(transaction.amount) >= 0 ? '+' : ''}{transaction.amount}
                      </div>
                      <p className="text-sm text-slate-500">{transaction.wallet?.currency || 'N/A'}</p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="p-12 text-center">
              <i className="bi bi-graph-up text-6xl text-slate-300 mb-4 block"></i>
              <h4 className="text-lg font-medium text-slate-900 mb-2">No transactions yet</h4>
              <p className="text-slate-500">Start by creating a wallet and making your first transaction</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
