import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';

const KYC = () => {
  const [documents, setDocuments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showUploadModal, setShowUploadModal] = useState(false);

  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/kyc/');
      setDocuments(response.data);
    } catch (error) {
      console.error('Error fetching KYC documents:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleUpload = async (formData) => {
    try {
      await axios.post('http://localhost:8000/api/kyc/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      toast.success('Document uploaded successfully');
      setShowUploadModal(false);
      fetchDocuments();
    } catch (error) {
      toast.error('Failed to upload document');
    }
  };

  const getStatusBadge = (status) => {
    const colors = {
      PENDING: 'bg-yellow-100 text-yellow-800',
      APPROVED: 'bg-green-100 text-green-800',
      REJECTED: 'bg-red-100 text-red-800',
    };

    return (
      <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${colors[status]}`}>
        {status}
      </span>
    );
  };

  if (loading) {
    return <div className="flex justify-center items-center h-64">Loading...</div>;
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h1 className="text-3xl font-bold text-gray-900">KYC Documents</h1>
        <button
          onClick={() => setShowUploadModal(true)}
          className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700"
        >
          Upload Document
        </button>
      </div>

      <div className="bg-white shadow overflow-hidden sm:rounded-md">
        <ul className="divide-y divide-gray-200">
          {documents.map((doc) => (
            <li key={doc.id}>
              <div className="px-4 py-4 sm:px-6">
                <div className="flex items-center justify-between">
                  <div className="flex items-center">
                    <div className="flex-shrink-0">
                      <img
                        className="h-16 w-16 object-cover rounded-lg"
                        src={`http://localhost:8000${doc.document}`}
                        alt={doc.doc_type}
                      />
                    </div>
                    <div className="ml-4">
                      <div className="flex items-center">
                        <p className="text-sm font-medium text-gray-900">
                          {doc.doc_type}
                        </p>
                        <div className="ml-2">
                          {getStatusBadge(doc.status)}
                        </div>
                      </div>
                      <p className="text-sm text-gray-500">
                        Uploaded: {new Date(doc.created_at).toLocaleDateString()}
                      </p>
                      {doc.reviewed_at && (
                        <p className="text-sm text-gray-500">
                          Reviewed: {new Date(doc.reviewed_at).toLocaleDateString()}
                        </p>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            </li>
          ))}
        </ul>

        {documents.length === 0 && (
          <div className="text-center py-12">
            <p className="text-gray-500">No documents uploaded yet</p>
            <button
              onClick={() => setShowUploadModal(true)}
              className="mt-4 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700"
            >
              Upload Your First Document
            </button>
          </div>
        )}
      </div>

      {showUploadModal && (
        <UploadModal
          onClose={() => setShowUploadModal(false)}
          onSubmit={handleUpload}
        />
      )}
    </div>
  );
};

const UploadModal = ({ onClose, onSubmit }) => {
  const [formData, setFormData] = useState({
    doc_type: '',
    document: null,
  });

  const handleSubmit = (e) => {
    e.preventDefault();

    const data = new FormData();
    data.append('doc_type', formData.doc_type);
    data.append('document', formData.document);

    onSubmit(data);
  };

  const documentTypes = [
    'National ID',
    'Passport',
    'Driver License',
    'Utility Bill',
    'Bank Statement',
    'Other'
  ];

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white p-6 rounded-lg w-96">
        <h2 className="text-xl font-bold mb-4">Upload KYC Document</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-sm font-medium mb-2">Document Type</label>
            <select
              value={formData.doc_type}
              onChange={(e) => setFormData({...formData, doc_type: e.target.value})}
              className="w-full p-2 border rounded"
              required
            >
              <option value="">Select Document Type</option>
              {documentTypes.map(type => (
                <option key={type} value={type}>{type}</option>
              ))}
            </select>
          </div>

          <div className="mb-4">
            <label className="block text-sm font-medium mb-2">Document File</label>
            <input
              type="file"
              accept="image/*"
              onChange={(e) => setFormData({...formData, document: e.target.files[0]})}
              className="w-full p-2 border rounded"
              required
            />
          </div>

          <div className="flex space-x-2">
            <button 
              type="submit" 
              className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
            >
              Upload
            </button>
            <button 
              type="button" 
              onClick={onClose} 
              className="bg-gray-300 px-4 py-2 rounded hover:bg-gray-400"
            >
              Cancel
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default KYC;
