import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

const Dashboard = () => {
  const [wallets, setWallets] = useState([]);
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [walletsRes, transactionsRes] = await Promise.all([
        axios.get('http://localhost:8000/api/wallets/'),
        axios.get('http://localhost:8000/api/transactions/')
      ]);

      setWallets(walletsRes.data);
      setTransactions(transactionsRes.data.slice(0, 10));
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const totalBalance = wallets.reduce((sum, wallet) => {
    // Convert all to USD for display (simplified)
    const rates = { NGN: 0.0024, KES: 0.0075, BTC: 45000, ETH: 3000 };
    return sum + (parseFloat(wallet.balance) * (rates[wallet.currency] || 1));
  }, 0);

  if (loading) {
    return <div className="flex justify-center items-center h-64">Loading...</div>;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="px-6 py-4">
          <h1 className="text-3xl font-bold text-gray-900">Welcome to BILLPAY</h1>
          <p className="text-gray-600">Manage your multi-currency wallet</p>
        </div>
      </div>

      <div className="p-6 space-y-6">
        {/* Total Balance Card */}
        <div className="bg-gradient-to-r from-blue-600 to-purple-600 p-8 rounded-2xl shadow-xl text-white">
          <div className="flex items-center justify-between">
            <div>
              <h2 className="text-lg font-medium opacity-90">Total Portfolio Value</h2>
              <p className="text-4xl font-bold">${totalBalance.toFixed(2)}</p>
              <p className="text-sm opacity-75 mt-1">Across all currencies</p>
            </div>
            <div className="text-6xl opacity-20">üí∞</div>
          </div>
        </div>

        {/* Wallet Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {wallets.map(wallet => {
            const getCurrencyIcon = (currency) => {
              const icons = { NGN: '‚Ç¶', KES: 'KSh', BTC: '‚Çø', ETH: 'Œû' };
              return icons[currency] || currency;
            };

            const getCurrencyColor = (currency) => {
              const colors = {
                NGN: 'from-green-400 to-green-600',
                KES: 'from-orange-400 to-red-500', 
                BTC: 'from-yellow-400 to-orange-500',
                ETH: 'from-purple-400 to-blue-500'
              };
              return colors[currency] || 'from-gray-400 to-gray-600';
            };

            return (
              <div key={wallet.id} className={`bg-gradient-to-br ${getCurrencyColor(wallet.currency)} p-6 rounded-xl shadow-lg text-white transform hover:scale-105 transition-transform`}>
                <div className="flex items-center justify-between mb-4">
                  <h3 className="text-lg font-semibold">{wallet.currency}</h3>
                  <span className="text-2xl">{getCurrencyIcon(wallet.currency)}</span>
                </div>
                <p className="text-2xl font-bold">
                  {parseFloat(wallet.balance).toFixed(wallet.currency.includes('BTC') || wallet.currency.includes('ETH') ? 6 : 2)}
                </p>
                <p className="text-sm opacity-75">{wallet.currency} Wallet</p>
              </div>
            );
          })}
        </div>

        {/* Quick Actions */}
        <div className="bg-white p-6 rounded-xl shadow-lg">
          <h2 className="text-2xl font-bold text-gray-900 mb-6">Quick Actions</h2>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <button className="bg-gradient-to-r from-blue-500 to-blue-600 text-white p-4 rounded-xl hover:from-blue-600 hover:to-blue-700 transform hover:scale-105 transition-all shadow-lg">
              <div className="text-2xl mb-2">üí∏</div>
              <div className="font-semibold">Send Money</div>
            </button>
            <button className="bg-gradient-to-r from-green-500 to-green-600 text-white p-4 rounded-xl hover:from-green-600 hover:to-green-700 transform hover:scale-105 transition-all shadow-lg">
              <div className="text-2xl mb-2">üí≥</div>
              <div className="font-semibold">Deposit</div>
            </button>
            <button className="bg-gradient-to-r from-yellow-500 to-orange-500 text-white p-4 rounded-xl hover:from-yellow-600 hover:to-orange-600 transform hover:scale-105 transition-all shadow-lg">
              <div className="text-2xl mb-2">üèß</div>
              <div className="font-semibold">Withdraw</div>
            </button>
            <button className="bg-gradient-to-r from-purple-500 to-pink-500 text-white p-4 rounded-xl hover:from-purple-600 hover:to-pink-600 transform hover:scale-105 transition-all shadow-lg">
              <div className="text-2xl mb-2">üîÑ</div>
              <div className="font-semibold">Convert</div>
            </button>
          </div>
        </div>

        {/* Recent Transactions */}
        <div className="bg-white rounded-xl shadow-lg overflow-hidden">
          <div className="p-6 border-b border-gray-200">
            <h2 className="text-2xl font-bold text-gray-900">Recent Activity</h2>
          </div>

          {transactions.length > 0 ? (
            <div className="divide-y divide-gray-200">
              {transactions.map(transaction => (
                <div key={transaction.id} className="p-6 hover:bg-gray-50 transition-colors">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-4">
                      <div className={`w-12 h-12 rounded-full flex items-center justify-center ${
                        parseFloat(transaction.amount) >= 0 ? 'bg-green-100 text-green-600' : 'bg-red-100 text-red-600'
                      }`}>
                        {parseFloat(transaction.amount) >= 0 ? '‚ÜóÔ∏è' : '‚ÜôÔ∏è'}
                      </div>
                      <div>
                        <p className="font-semibold text-gray-900">{transaction.type}</p>
                        <p className="text-sm text-gray-500">
                          {new Date(transaction.created_at).toLocaleDateString()}
                        </p>
                      </div>
                    </div>
                    <div className="text-right">
                      <p className={`font-bold ${
                        parseFloat(transaction.amount) >= 0 ? 'text-green-600' : 'text-red-600'
                      }`}>
                        {parseFloat(transaction.amount) >= 0 ? '+' : ''}{transaction.amount}
                      </p>
                      <p className="text-sm text-gray-500">{transaction.wallet?.currency || 'N/A'}</p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="p-12 text-center">
              <div className="text-6xl mb-4">üìä</div>
              <h3 className="text-xl font-semibold text-gray-900 mb-2">No transactions yet</h3>
              <p className="text-gray-500">Start by creating a wallet and making your first transaction</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
