import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

const Dashboard = () => {
  const [wallets, setWallets] = useState([]);
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [walletsRes, transactionsRes] = await Promise.all([
        axios.get('http://localhost:8000/api/wallets/'),
        axios.get('http://localhost:8000/api/transactions/')
      ]);

      setWallets(walletsRes.data);
      setTransactions(transactionsRes.data.slice(0, 10));
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const totalBalance = wallets.reduce((sum, wallet) => {
    // Convert all to USD for display (simplified)
    const rates = { NGN: 0.0024, KES: 0.0075, BTC: 45000, ETH: 3000 };
    return sum + (parseFloat(wallet.balance) * (rates[wallet.currency] || 1));
  }, 0);

  if (loading) {
    return <div className="flex justify-center items-center h-64">Loading...</div>;
  }

  return (
    <div className="bg-light min-vh-100">
      {/* Header */}
      <div className="bg-white shadow-sm border-bottom mb-4">
        <div className="container-fluid py-3">
          <h1 className="h2 fw-bold text-primary mb-1">Welcome to BILLPAY</h1>
          <p className="text-muted mb-0">Manage your multi-currency wallet</p>
        </div>
      </div>

      <div className="container-fluid">
        {/* Total Balance Card */}
        <div className="row mb-4">
          <div className="col-12">
            <div className="card bg-primary text-white shadow-lg">
              <div className="card-body p-4">
                <div className="row align-items-center">
                  <div className="col-8">
                    <h5 className="card-title opacity-75 mb-2">Total Portfolio Value</h5>
                    <h1 className="display-4 fw-bold mb-1">${totalBalance.toFixed(2)}</h1>
                    <small className="opacity-75">Across all currencies</small>
                  </div>
                  <div className="col-4 text-end">
                    <i className="bi bi-wallet2 display-1 opacity-25"></i>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Wallet Cards */}
        <div className="row mb-4">
          {wallets.map(wallet => {
            const getCurrencyIcon = (currency) => {
              const icons = { NGN: 'bi-currency-exchange', KES: 'bi-cash-coin', BTC: 'bi-currency-bitcoin', ETH: 'bi-currency-exchange' };
              return icons[currency] || 'bi-wallet';
            };

            const getCurrencyColor = (currency) => {
              const colors = {
                NGN: 'success',
                KES: 'warning', 
                BTC: 'warning',
                ETH: 'info'
              };
              return colors[currency] || 'secondary';
            };

            return (
              <div key={wallet.id} className="col-lg-3 col-md-6 mb-3">
                <div className={`card bg-${getCurrencyColor(wallet.currency)} text-white h-100 shadow`}>
                  <div className="card-body">
                    <div className="d-flex justify-content-between align-items-center mb-3">
                      <h5 className="card-title mb-0">{wallet.currency}</h5>
                      <i className={`${getCurrencyIcon(wallet.currency)} fs-2`}></i>
                    </div>
                    <h3 className="fw-bold mb-1">
                      {parseFloat(wallet.balance).toFixed(wallet.currency.includes('BTC') || wallet.currency.includes('ETH') ? 6 : 2)}
                    </h3>
                    <small className="opacity-75">{wallet.currency} Wallet</small>
                  </div>
                </div>
              </div>
            );
          })}
        </div>

        {/* Quick Actions */}
        <div className="row mb-4">
          <div className="col-12">
            <div className="card shadow">
              <div className="card-body">
                <h4 className="card-title mb-4">Quick Actions</h4>
                <div className="row g-3">
                  <div className="col-lg-3 col-md-6">
                    <button className="btn btn-primary w-100 p-3 h-100">
                      <i className="bi bi-send fs-2 d-block mb-2"></i>
                      <span className="fw-semibold">Send Money</span>
                    </button>
                  </div>
                  <div className="col-lg-3 col-md-6">
                    <button className="btn btn-success w-100 p-3 h-100">
                      <i className="bi bi-plus-circle fs-2 d-block mb-2"></i>
                      <span className="fw-semibold">Deposit</span>
                    </button>
                  </div>
                  <div className="col-lg-3 col-md-6">
                    <button className="btn btn-warning w-100 p-3 h-100">
                      <i className="bi bi-dash-circle fs-2 d-block mb-2"></i>
                      <span className="fw-semibold">Withdraw</span>
                    </button>
                  </div>
                  <div className="col-lg-3 col-md-6">
                    <button className="btn btn-info w-100 p-3 h-100">
                      <i className="bi bi-arrow-repeat fs-2 d-block mb-2"></i>
                      <span className="fw-semibold">Convert</span>
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Recent Transactions */}
        <div className="row">
          <div className="col-12">
            <div className="card shadow">
              <div className="card-header bg-white">
                <h4 className="card-title mb-0">Recent Activity</h4>
              </div>

              {transactions.length > 0 ? (
                <div className="card-body p-0">
                  {transactions.map(transaction => (
                    <div key={transaction.id} className="border-bottom p-3 hover-bg-light">
                      <div className="row align-items-center">
                        <div className="col-auto">
                          <div className={`rounded-circle d-flex align-items-center justify-content-center ${
                            parseFloat(transaction.amount) >= 0 ? 'bg-success bg-opacity-10 text-success' : 'bg-danger bg-opacity-10 text-danger'
                          }`} style={{width: '48px', height: '48px'}}>
                            <i className={`bi ${
                              parseFloat(transaction.amount) >= 0 ? 'bi-arrow-up-right' : 'bi-arrow-down-left'
                            } fs-5`}></i>
                          </div>
                        </div>
                        <div className="col">
                          <h6 className="mb-1">{transaction.type}</h6>
                          <small className="text-muted">
                            {new Date(transaction.created_at).toLocaleDateString()}
                          </small>
                        </div>
                        <div className="col-auto text-end">
                          <div className={`fw-bold ${
                            parseFloat(transaction.amount) >= 0 ? 'text-success' : 'text-danger'
                          }`}>
                            {parseFloat(transaction.amount) >= 0 ? '+' : ''}{transaction.amount}
                          </div>
                          <small className="text-muted">{transaction.wallet?.currency || 'N/A'}</small>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="card-body text-center py-5">
                  <i className="bi bi-graph-up display-1 text-muted mb-3"></i>
                  <h5 className="text-muted mb-2">No transactions yet</h5>
                  <p className="text-muted">Start by creating a wallet and making your first transaction</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
