import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [accounts, setAccounts] = useState([]);
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: { Authorization: `Bearer ${token}` }
      };

      const [walletsRes, transactionsRes] = await Promise.all([
        axios.get('http://localhost:8000/api/wallets/', config),
        axios.get('http://localhost:8000/api/transactions/', config)
      ]);

      setAccounts(walletsRes.data);
      setTransactions(transactionsRes.data.slice(0, 8));
    } catch (error) {
      console.error('Error fetching data:', error);
    } finally {
      setLoading(false);
    }
  };

  const getAccountFeatures = (currency) => {
    const features = {
      NGN: ['Deposit', 'Withdraw', 'Convert to Crypto'],
      KES: ['Deposit', 'Withdraw', 'Convert to Crypto'],
      BTC: ['Exchange Only'],
      ETH: ['Exchange Only']
    };
    return features[currency] || [];
  };

  const getAccountIcon = (currency) => {
    const icons = {
      NGN: 'bank',
      KES: 'phone',
      BTC: 'currency-bitcoin',
      ETH: 'currency-exchange'
    };
    return icons[currency] || 'wallet';
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-slate-900 flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  const fiatAccounts = accounts.filter(acc => ['NGN', 'KES'].includes(acc.currency));
  const cryptoAccounts = accounts.filter(acc => ['BTC', 'ETH'].includes(acc.currency));

  return (
    <div className="min-h-screen bg-slate-900">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">

        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-white mb-2">My Accounts</h1>
          <p className="text-slate-400">Manage your fiat and crypto accounts</p>
        </div>

        {/* Fiat Accounts (NGN & KES) */}
        <div className="mb-8">
          <h2 className="text-xl font-bold text-white mb-4">Fiat Accounts</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {fiatAccounts.map(account => (
              <div key={account.id} className="bg-slate-800 rounded-xl p-6 shadow-lg border border-slate-700">
                <div className="flex items-center justify-between mb-4">
                  <div className="flex items-center space-x-3">
                    <div className={`w-12 h-12 ${account.currency === 'NGN' ? 'bg-green-600' : 'bg-orange-600'} rounded-lg flex items-center justify-center`}>
                      <i className={`bi bi-${getAccountIcon(account.currency)} text-white text-lg`}></i>
                    </div>
                    <div>
                      <h3 className="text-lg font-bold text-white">{account.currency} Account</h3>
                      <p className="text-slate-400 text-sm">{account.currency === 'NGN' ? 'Nigerian Naira' : 'Kenyan Shilling'}</p>
                    </div>
                  </div>
                </div>

                <div className="mb-4">
                  <p className="text-slate-400 text-sm mb-1">Available Balance</p>
                  <h2 className="text-2xl font-bold text-white">
                    {parseFloat(account.balance).toFixed(2)} {account.currency}
                  </h2>
                </div>

                <div className="grid grid-cols-3 gap-2">
                  <button className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-2 rounded-lg text-sm font-medium transition-colors">
                    Deposit
                  </button>
                  <button className="bg-red-600 hover:bg-red-700 text-white px-3 py-2 rounded-lg text-sm font-medium transition-colors">
                    Withdraw
                  </button>
                  <button className="bg-purple-600 hover:bg-purple-700 text-white px-3 py-2 rounded-lg text-sm font-medium transition-colors">
                    Convert
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Crypto Accounts */}
        <div className="mb-8">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-xl font-bold text-white">Crypto Accounts</h2>
            <button className="bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition-all">
              + Create Crypto Account
            </button>
          </div>

          {cryptoAccounts.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {cryptoAccounts.map(account => (
                <div key={account.id} className="bg-slate-800 rounded-xl p-6 shadow-lg border border-slate-700">
                  <div className="flex items-center justify-between mb-4">
                    <div className="flex items-center space-x-3">
                      <div className={`w-12 h-12 ${account.currency === 'BTC' ? 'bg-yellow-600' : 'bg-indigo-600'} rounded-lg flex items-center justify-center`}>
                        <i className={`bi bi-${getAccountIcon(account.currency)} text-white text-lg`}></i>
                      </div>
                      <div>
                        <h3 className="text-lg font-bold text-white">{account.currency}</h3>
                        <p className="text-slate-400 text-sm">{account.currency === 'BTC' ? 'Bitcoin' : 'Ethereum'}</p>
                      </div>
                    </div>
                  </div>

                  <div className="mb-4">
                    <p className="text-slate-400 text-sm mb-1">Balance</p>
                    <h2 className="text-xl font-bold text-white">
                      {parseFloat(account.balance).toFixed(6)} {account.currency}
                    </h2>
                  </div>

                  <button className="w-full bg-slate-700 hover:bg-slate-600 text-white px-3 py-2 rounded-lg text-sm font-medium transition-colors">
                    Exchange Only
                  </button>
                </div>
              ))}
            </div>
          ) : (
            <div className="bg-slate-800 rounded-xl p-8 text-center border border-slate-700">
              <i className="bi bi-currency-bitcoin text-4xl text-slate-600 mb-4 block"></i>
              <h3 className="text-lg font-medium text-white mb-2">No Crypto Accounts</h3>
              <p className="text-slate-400 mb-4">Create Bitcoin or Ethereum accounts for crypto trading</p>
              <button className="bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white px-6 py-2 rounded-lg font-medium transition-all">
                Create Your First Crypto Account
              </button>
            </div>
          )}
        </div>

        {/* Recent Activity */}
        <div className="bg-slate-800 rounded-xl shadow-lg border border-slate-700">
          <div className="p-6 border-b border-slate-700">
            <h3 className="text-xl font-bold text-white">Recent Activity</h3>
          </div>

          {transactions.length > 0 ? (
            <div className="divide-y divide-slate-700">
              {transactions.map(transaction => (
                <div key={transaction.id} className="p-6 hover:bg-slate-700 transition-colors">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-4">
                      <div className={`w-12 h-12 rounded-full flex items-center justify-center ${
                        transaction.type === 'deposit' ? 'bg-green-500/20 text-green-400' :
                        transaction.type === 'withdrawal' ? 'bg-red-500/20 text-red-400' :
                        'bg-blue-500/20 text-blue-400'
                      }`}>
                        <i className={`bi ${
                          transaction.type === 'deposit' ? 'bi-arrow-down-left' :
                          transaction.type === 'withdrawal' ? 'bi-arrow-up-right' :
                          'bi-arrow-repeat'
                        } text-lg`}></i>
                      </div>
                      <div>
                        <h4 className="font-medium text-white capitalize">{transaction.type}</h4>
                        <p className="text-sm text-slate-400">
                          {new Date(transaction.created_at).toLocaleDateString()}
                        </p>
                      </div>
                    </div>
                    <div className="text-right">
                      <div className="font-bold text-white">
                        {transaction.amount} {transaction.wallet?.currency || 'N/A'}
                      </div>
                      <p className="text-sm text-slate-400">
                        {transaction.status || 'Completed'}
                      </p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="p-12 text-center">
              <i className="bi bi-clock-history text-6xl text-slate-600 mb-4 block"></i>
              <h4 className="text-lg font-medium text-white mb-2">No recent activity</h4>
              <p className="text-slate-400">Your transactions will appear here</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
